var msgOut = RED.util.cloneMessage(msg);

// store sprinkler duration from ui
if (msg.topic === "tcs/sprink/duration") {
    global.set("sprinklerDuration", msg.payload);
    return null;
}

msgOut.timerType = 'delay'; // 'delay', 'loop'
msgOut.timerDurationUnit = 'second';
msgOut.retain = false;

if (msg.topic === "tcs/sprink/in") {
    // prepare message from mqtt
    msgOut.topic = "tcs/sprink/out/" + msg.payload.number;
    msgOut.number = msg.payload.number;
    msgOut.payload = msg.payload.action;
    msgOut.timerDuration = msg.payload.dauer;

} else if (msg.topic.startsWith("tcs/sprink/switch/")) {
    // prepare message from ui switches
    var numberStr = msg.topic.split('/').pop();
    var number = parseInt(numberStr, 10);
    msgOut.topic = "tcs/sprink/out/" + String(number);
    msgOut.number = number;
    msgOut.payload = msg.payload;
    msgOut.timerDuration = global.get("sprinklerDuration");
}
return msgOut;

