const messages = new Array(node.outputCount)

if (msg.topic.startsWith("tcs/court/state/")) {
    // prepare state message from court
    var numberStr = msg.topic.split('/').pop();

    var sprinklers = msg.payload["sprinklers"];
    for (let i = 0; i < sprinklers.length; i++) {
        if (sprinklers[i] < 2) {
            messages[i] = { payload: Boolean(sprinklers[i]) };
        }
    }

    var floodlight = msg.payload["floodlight"];
    if (floodlight < 2) {
        // floodlight message at the end
        messages[7] = { payload: Boolean(floodlight) };
    }
}
return messages;
